package com.syntax.class03;

public class ShorthandOperatorsReview {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Declare variable cakePiece=11
		 * and divide the value of that variable between 4 people using shorthand operator 
		 */
		
		int cakePiece=11;
		cakePiece/=4;
		
		System.out.println(cakePiece);
		
		/*
		 * Declare variable cake=25 and divide cake between 7 people.Using shorthand operator after it was dis amoungest 7 people 
		 */
		
		int cake=25;
		System.out.println(cake/7);
		cake%=7;
		System.out.println(cake);

	}

}
